import React, { useEffect, useState } from 'react';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';
import { Container, Paper } from '@mui/material';
import Button from '@mui/material/Button';
import TableContainer from '@mui/material/TableContainer';


const criterias = [
    {
      value: 'Keywords',
      label: 'Keywords',
    },
    {
      value: 'Author',
      label: 'Author',
    },
  ];

export default function Data() {
    const paperStyle = {padding : '50px 20px', width : '70vw' , margin : '20px auto'}
    const[Search,setSearch] = React.useState('')
    const[Keyword,setKeyword] = React.useState('')
    const[searchResults,setSearchResults] = React.useState([])
    const [dep,setDep] = React.useState()
    const[subDep,setSubDep] = React.useState()
    const [departments, setDepartments] = useState([]);
    useEffect(() => {
      fetch('http://localhost:8080/api/v1/data/tables')
        .then(response => response.json())
        .then(data => {
          const departmentsData = data.map(departmentName => ({
            value: departmentName,
            label: departmentName
          }));
  
          // Add the "All" department option
          departmentsData.push({
            value: 'all',
            label: 'All'
          });
  
          setDepartments(departmentsData);
        })
        .catch(error => {
          console.error('Error fetching departments:', error);
        });
    }, []);
    const [subDeps, setSubDeps] = useState([]);
    useEffect(() => {
      fetch('http://localhost:8080/api/v1/data/captiveauto/field/coll')
        .then(response => response.json())
        .then(data => {
          const departmentsData = data.map(departmentName => ({
            value: departmentName,
            label: departmentName
          }));
  
          // Add the "All" department option
          departmentsData.push({
            value: 'all',
            label: 'All'
          });
  
          setSubDeps(departmentsData);
        })
        .catch(error => {
          console.error('Error fetching departments:', error);
        });
    }, []);

    const handleClick = (e)=> {
        const searchVal = {Search,Keyword};
        e.preventDefault()
        if (Keyword==='Author'){
          fetch(`http://localhost:8080/api/v1/data/search/${encodeURIComponent(Search)}?searchVal=${encodeURIComponent(searchVal)}`, {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json'
            }
          })
            .then(response => response.json())
            .then(data => {
              setSearchResults(data); // Update the search results state with the received data
            })
            .catch(error => {
              console.log("Some kind of error occurred:", error);
              console.log(searchResults);
            });
          
        } else{
          fetch(`http://localhost:8080/api/v1/data/searchDes/${encodeURIComponent(Search)}?searchVal=${encodeURIComponent(searchVal)}`, {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json'
            }
          })
            .then(response => response.json())
            .then(data => {
              setSearchResults(data); // Update the search results state with the received data
            })
            .catch(error => {
              console.log("Some kind of error occurred:", error);
              console.log(searchResults);
            });
        }
    }      

  return (
    <Container>
    <Paper elevation={3} style={paperStyle}>
        <h1 
        style={{fontFamily : 'sans-serif', fontSize : '2em'}}
        > Search the Database</h1>
        <p>Searching for the {Keyword} named {Search}</p>
    <form
      component="form"
      noValidate
      autoComplete="off"
    >
        <TextField
          id="filled-select-currency"
          style={{marginRight : '0.5em', width : '10vw'}}
          select
          label="Select"
          defaultValue="Keywords"
          helperText="Search Category"
          variant="filled"
          value={Keyword}
          onChange={(e)=> setKeyword(e.target.value)}
        >
          {criterias.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))}
        </TextField>
        <TextField
          required
          id="filled-required"
          style={{width : '50vw'}}
          label="Required"
          variant="filled"
          value={Search}
          onChange={(e)=> setSearch(e.target.value)}
        />
    </form>
    <div 
    style={{ display :'grid', 
    gridTemplateColumns :'0.5fr 0.5fr 1fr',
    marginTop : '2em',
    marginLeft : '5em'
  }}
    >
    <TextField
            id="filled-select-currency"
            style={{ margin: 'auto', width: '10vw' }}
            defaultValue={""}
            select
            label="Department"
            value={dep}
            onChange={(e) => setDep(e.target.value)}
          >
            {departments.map((option) => (
              <MenuItem key={option.value} value={option.value}>
                {option.label}
              </MenuItem>
            ))}
          </TextField>
        <TextField
          id="filled-select-currency"
          style={{margin : 'auto', width : '10vw'}}
          defaultValue={"Java"}
          select
          label="Group"
          value={subDep}
          onChange={(e)=> setSubDep(e.target.value)}
        >
          {subDeps.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))}
        </TextField>
    <Button variant="contained" disabled={!{Search}} onClick={handleClick}
    style={{width : 'fit-content'}}
    >
         Search
        </Button>
    </div>
    </Paper>

    <div>
        {searchResults.map((result) => (
          <div key={result.id} className="box-container">
            {/* Render the content of each Data object */}
            <TableContainer component={Paper} sx={{maxWidth:'30vw',margin : 'auto',marginBottom : '2em'}}>
            <table sx={{ minWidth: 650 }} aria-label="simple table">
        <thead>
            <tr><th colspan="2">{result.id}</th></tr>
        </thead>
        <tbody>
            <tr>
                <td><strong>Author</strong></td>
                <td><span  style={{fontSize : '1em',}}>{result.author}</span></td>
            </tr>
            <tr>
                <td><strong>Date Created</strong></td>
                <td><span  style={{fontSize : '1em',}}>{result.date_Cr}</span></td>
            </tr>
            <tr>
                <td><strong>Date Modified</strong></td>
                <td><span style={{fontSize : '1em',}}>{result.date_Md}</span></td>
            </tr>
            <tr>
                <td><strong>Description</strong></td>
                <td><span style={{fontSize : '1em',}}>{result.description}</span></td>
            </tr>
	    </tbody>
	    </table>
            </TableContainer>
          </div>
        ))}
      </div>
      <div style={{height: "30px"}} />
    </Container>

  );
}
