import React, { useState, useEffect } from 'react';
import Paper from '@mui/material/Paper';
import { useParams } from 'react-router-dom';
import { Typography } from '@mui/material';
import { styled } from '@mui/system';
import { Link } from 'react-router-dom';
import { Document, Page, pdfjs } from 'react-pdf';

pdfjs.GlobalWorkerOptions.workerSrc = `https://cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;



const View = () => {
    const { id } = useParams();

    const [searchRes, setSearchRes] = useState([]);
    const [fileContents, setFileContents] = useState({});
    const [numPages, setNumPages] = useState(null);


    const getFileContent = async (fileName) => {
        try {
            const response = await fetch(`http://localhost:8080/files/${fileName}.txt`);
            if (response.ok) {
                return await response.text();
            } else {
                console.error('Error retrieving file content:', response.status);
                return '';
            }
        } catch (error) {
            console.error('Error fetching data:', error);
            return '';
        }
    };

    const getExtension = (path) => {
        const baseName = path.split(/[\\/]/).pop();
        const pos = baseName.lastIndexOf('.');
        if (baseName === '' || pos < 1) {
            return '';
        }
        return baseName.slice(pos + 1);
    };

    useEffect(() => {
        fetch(`http://localhost:8080/search/${id}`)
            .then((response) => response.json())
            .then((data) => {
                setSearchRes(data);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });

        const fetchFileContents = async () => {
            const fileContentPromises = searchRes.map((item) =>
                getFileContent(item.fileName)
            );

            const fileContentsArray = await Promise.all(fileContentPromises);

            const fileContentsMap = searchRes.reduce(
                (acc, item, index) => ({
                    ...acc,
                    [item.fileName]: fileContentsArray[index],
                }),
                {}
            );

            setFileContents(fileContentsMap);
        };

        fetchFileContents();
    }, [searchRes, id]);

    const renderFile = (item) => {
        const extension = getExtension(item.filePath);
        const fileUrl = `http://localhost:8080/files/${item.fileName}.${extension}`;

        switch (extension) {
            case 'txt':
                return <pre>{fileContents[item.fileName]}</pre>;
            case 'jpg':
            case 'jpeg':
            case 'png':
                return <img src={fileUrl} alt={item.fileName} />;
            case 'mp4':
                return (
                    <video controls>
                        <source src={fileUrl} type="video/mp4" />
                    </video>
                );
            case 'mp3':
                return (
                    <audio controls>
                        <source src={fileUrl} type="audio/mpeg" />
                    </audio>
                );
            case 'pdf':
                return (
                    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
                        <Document file={fileUrl} onLoadSuccess={({ numPages }) => setNumPages(numPages)}>
                            {Array.from(new Array(numPages), (_, index) => (
                                <Page key={`page_${index + 1}`} pageNumber={index + 1} width={window.innerWidth * 0.8} />
                            ))}
                        </Document>
                    </div>
                );
            case 'docx':
                return (
                    <iframe src={`https://view.officeapps.live.com/op/embed.aspx?src=${fileUrl}`} width="100%" height="600px" frameBorder="0" />
                );
            case 'xlsx':
                return (
                    <iframe src={`https://view.officeapps.live.com/op/embed.aspx?src=${fileUrl}`} width="100%" height="600px" frameBorder="0" />
                );
            default:
                return <Typography>Unsupported file format</Typography>;
        }
    };

    return (
        <div>
            {searchRes.map((item) => (
                <div key={item.id}>
                    {renderFile(item)}
                </div>
            ))}
        </div>
    );
};

export default View;
