import * as React from 'react';
import Paper from '@mui/material/Paper';
import { styled } from '@mui/system'; // Import from @mui/system instead of @mui/material/styles
import { useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import Appbar from './components/Appbar';
import Button from '@mui/material/Button';
import ButtonGroup from '@mui/material/ButtonGroup';
import './index.css';
import TextSnippetIcon from '@mui/icons-material/TextSnippet';
import { Typography } from '@mui/material';

const Details = () => {
  const { id } = useParams();

  function getExtension(path) {
    let baseName = path.split(/[\\/]/).pop(), // extracts file name from full path
      // (supports separators `\\` and `/`)
      pos = baseName.lastIndexOf("."); // gets the last position of `.`
    if (baseName === "" || pos < 1) // if the file name is empty or ...
      return ""; // the dot not found (-1) or comes first (0)
    return baseName.slice(pos + 1); // extracts extension ignoring "."
  }

  const [searchRes, setSearchRes] = useState([]);

  const [fileContents, setFileContents] = useState({});
  const [selectedCategory, setSelectedCategory] = useState(''); // Added state for selected category

  const getFileContent = async (fileName) => {
    try {
      const response = await fetch(`http://localhost:8080/files/${fileName}.txt`);
      if (response.ok) {
        return await response.text();
      } else {
        console.error('Error retrieving file content:', response.status);
        return '';
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      return '';
    }
  };
  useEffect(() => {
    fetch(`http://localhost:8080/search/menu/${id}`)
      .then(response => response.json())
      .then(data => {
        setSearchRes(data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });

    // Fetch file contents for the selected category
    const fetchFileContents = async () => {
      const selectedFiles = searchRes.filter(item => {
        if (selectedCategory === 'media') {
          return item.filePath.endsWith('.mp4') || item.filePath.endsWith('.mp3');
        } else if (selectedCategory === 'docs') {
          return item.filePath.endsWith('.txt') || item.filePath.endsWith('.pdf');
        } else if (selectedCategory === 'visuals') {
          return (
            item.filePath.endsWith('.jpg') ||
            item.filePath.endsWith('.jpeg') ||
            item.filePath.endsWith('.png')
          );
        }
        return false;
      });

      const fileContentPromises = selectedFiles.map(item =>
        getFileContent(item.fileName)
      );

      const fileContentsArray = await Promise.all(fileContentPromises);

      const fileContentsMap = selectedFiles.reduce(
        (acc, item, index) => ({
          ...acc,
          [item.fileName]: fileContentsArray[index],
        }),
        {}
      );

      setFileContents(fileContentsMap);
    };

    fetchFileContents();

  }, [searchRes, id, selectedCategory]);

  const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    flexGrow: 1,
  }));

  const myStyle = {
    //         backgroundImage: 
    //  "url('https://media.geeksforgeeks.org/wp-content/uploads/rk.png')",
    backgroundColor: "#eb8934",
    minHeight: "100vh",
    backgroundSize: 'cover',
    backgroundRepeat: 'repeat',
  }

  return (
    <div style={myStyle}>
      <div> <Appbar className="App" /> </div>
      <div style={{ backgroundColor: "#eb8934", height: '3cm' }}></div>
      {searchRes.length > 0 && (
        <Paper style={{
          display: 'grid',
          gridTemplateColumns: '1fr 1fr 1fr',
          padding: '2em',
        }}>
          <div><h4>Department Name:</h4> {searchRes[0].menu.application.function.department.departmentName}</div>
          <div><h4>Function Name:</h4> {searchRes[0].menu.application.function.functionName}</div>
          <div><h4>Application Name:</h4> {searchRes[0].menu.application.applicationName}</div>
          <div><h4>Menu Name:</h4> {searchRes[0].menu.menuName}</div>
          <div><h4>Author:</h4> {searchRes[0].author}</div>
          <div><h4>Description:</h4> {searchRes[0].menu.description}</div>
        </Paper>
      )}
      <div>
        <ButtonGroup variant="contained" aria-label="outlined primary button group"
          style={{
            padding: '2em', display: 'flex', justifyContent: 'center',
            gap: '20px'
          }}
        >
          <Button onClick={() => setSelectedCategory('media')}>Media</Button>
          <Button onClick={() => setSelectedCategory('docs')}>Documents</Button>
          <Button onClick={() => setSelectedCategory('visuals')}>Visuals</Button>
        </ButtonGroup>
      </div>
      {searchRes.map(item => (
        <div key={item.id}>
          {selectedCategory === 'media' &&
            (item.filePath.endsWith('.mp4') || item.filePath.endsWith('.mp3')) && (
              <Paper style={{ myStyle }}>
                <TextSnippetIcon />
                <Typography>Hello</Typography>
                Hello World
              </Paper>
            )}
          {selectedCategory === 'docs' &&
            (item.filePath.endsWith('.txt') || item.filePath.endsWith('.pdf')) && (
                <div style={{display : 'grid', gridTemplateColumns : '1fr 1fr' , width : '30vw'}}>
                  <TextSnippetIcon />
                <Typography>{item.fileName}</Typography>
                </div>
            )}
          {selectedCategory === 'visuals' &&
            (item.filePath.endsWith('.jpg') ||
              item.filePath.endsWith('.jpeg') ||
              item.filePath.endsWith('.png')) && (
              <Paper style={{ myStyle }}>
                {item.filePath.endsWith('.jpg') || item.filePath.endsWith('.jpeg') || item.filePath.endsWith('.png') ? (
                  // eslint-disable-next-line jsx-a11y/alt-text
                  <img src={`http://localhost:8080/files/${item.fileName}.` + `${getExtension(item.filePath)}`} />
                ) : null}
              </Paper>
            )}
        </div>
      ))}
    </div>
  );
};

export default Details;
