import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';

const CSVRenderer = ({ csvUrl }) => {
  const [csvData, setCsvData] = useState('');

  useEffect(() => {
    const fetchCSVData = async () => {
      try {
        const response = await fetch(csvUrl);
        const text = await response.text();
        setCsvData(text);
      } catch (error) {
        console.error('Error fetching CSV data:', error);
      }
    };

    fetchCSVData();
  }, [csvUrl]);

  const renderCsvData = () => {
    const parsedData = Papa.parse(csvData, {
      header: true, // Treat the first row as headers
    });

    if (parsedData.errors.length === 0) {
      const rows = parsedData.data.map((row, index) => (
        <tr key={index}>
          {Object.values(row).map((value, colIndex) => (
            <td key={colIndex}>{value}</td>
          ))}
        </tr>
      ));

      return (
        <table>
          <thead>
            <tr>
              {parsedData.meta.fields.map((field, index) => (
                <th key={index}>{field}</th>
              ))}
            </tr>
          </thead>
          <tbody>{rows}</tbody>
        </table>
      );
    } else {
      // Handle CSV parsing errors if any
      return <div>Failed to parse CSV data</div>;
    }
  };

  return (
    <div>
      {csvData ? renderCsvData() : <div>Loading CSV data...</div>}
    </div>
  );
};

export default CSVRenderer;
