import * as React from 'react';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';
import { Container, Paper } from '@mui/material';
import Button from '@mui/material/Button';

const criterias = [
    {
      value: 'Keywords',
      label: 'Keywords',
    },
    {
      value: 'Author',
      label: 'Author',
    },
  ];

export default function Data() {
    const paperStyle = {padding : '50px 20px', width : '70vw' , margin : '20px auto'}
    const[Search,setSearch] = React.useState('')
    const[Keyword,setKeyword] = React.useState('')


  return (
    <Container>
    <Paper elevation={3} style={paperStyle}>
        <h1 
        style={{fontFamily : 'sans-serif', fontSize : '2em'}}
        > Search the Database</h1>
        <p>Searching for the {Keyword} named {Search}</p>
    <form
      component="form"
      noValidate
      autoComplete="off"
    >
        <TextField
          id="filled-select-currency"
          style={{marginRight : '0.5em', width : '10vw'}}
          select
          label="Select"
          defaultValue="Keywords"
          helperText="Search Category"
          variant="filled"
          value={Keyword}
          onChange={(e)=> setKeyword(e.target.value)}
        >
          {criterias.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))}
        </TextField>
        <TextField
          required
          id="filled-required"
          style={{width : '50vw'}}
          label="Required"
          variant="filled"
          value={Search}
          onChange={(e)=> setSearch(e.target.value)}
        />
         <Button variant="contained" href="#contained-buttons">
         Search
        </Button>
    </form>
    </Paper>
    </Container>
  );
}
