import React, { useState } from 'react';
import useAxios from 'axios';
import { TextField, Button, Typography } from '@mui/material';

const Signup = () => {
  const [userName, setUserName] = useState('');
  const [passWord, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const [{ loading }, executeSignup] = useAxios(
    {
      url: '/login/create',
      method: 'post',
    },
    { manual: true } // Do not trigger the request automatically
  );

  const handleSignup = async () => {
    try {
      const response = await executeSignup({
        params: { userName, passWord },
      });

      if (response.status === 200) {
        setErrorMessage('');
        setSuccessMessage('User created successfully!');
      }
    } catch (error) {
      if (error.response && error.response.status === 409) {
        setErrorMessage('A user with the username already exists. Please choose another username.');
        setSuccessMessage('');
      } else {
        setErrorMessage('Error occurred while creating the user.');
        setSuccessMessage('');
      }
    }
  };

  return (
    <div>
      <Typography variant="h5">Sign Up</Typography>
      <TextField
        label="Username"
        value={userName}
        onChange={(e) => setUserName(e.target.value)}
        fullWidth
        margin="normal"
      />
      <TextField
        label="Password"
        value={passWord}
        onChange={(e) => setPassword(e.target.value)}
        type="password"
        fullWidth
        margin="normal"
      />
      <Button variant="contained" color="primary" onClick={handleSignup} disabled={loading}>
        Sign Up
      </Button>
      {errorMessage && <Typography color="error">{errorMessage}</Typography>}
      {successMessage && <Typography color="success">{successMessage}</Typography>}
    </div>
  );
};

export default Signup;
