import React, { useState, useEffect } from 'react';
import { Typography } from '@mui/material';
import { Document, Page, pdfjs } from 'react-pdf';
import useAxiosPrivate from '../hooks/useAxiosPrivate';
import { useParams } from 'react-router-dom';
import Appbar from './Appbar';

pdfjs.GlobalWorkerOptions.workerSrc = `https://cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;

const View = () => {
    const { id } = useParams();
    const axiosPrivate = useAxiosPrivate();

    const [searchRes, setSearchRes] = useState([]);
    const [numPages, setNumPages] = useState(null);

    const [isFileRendered, setIsFileRendered] = useState(false);

    useEffect(() => {
        const fetchSearchResults = async () => {
            try {
                const response = await axiosPrivate.get(`http://localhost:8080/search/${id}`);
                setSearchRes(response.data);
            } catch (error) {
                console.error('Error fetching search results:', error);
                setSearchRes([]);
            }
        };

        fetchSearchResults();
    }, [id, axiosPrivate]);

    const getFileContent = async (fileName, menuId) => {
        try {
            const response = await axiosPrivate.get(`http://localhost:8080/files/${fileName}/menu/${menuId}`);
    
            if (response.status === 200) {
                return response.data; // Access the 'data' property to get the content
            } else {
                console.error('Error retrieving file content:', response.status);
                return '';
            }
        } catch (error) {
            console.error('Error fetching data:', error);
            return '';
        }
    };

    const fetchMediaFile = async (fileUrl) => {
        try {
            const response = await axiosPrivate.get(fileUrl, { responseType: 'blob' });
            return response.data;
        } catch (error) {
            console.error('Error fetching media file:', error);
            return null;
        }
    };

    const fetchPDFFile = async (fileUrl) => {
        try {
            const response = await axiosPrivate.get(fileUrl, { responseType: 'blob' });
            return response.data;
        } catch (error) {
            console.error('Error fetching PDF file:', error);
            return null;
        }
    };

    const getExtension = (path) => {
        const baseName = path.split(/[\\/]/).pop();
        const pos = baseName.lastIndexOf('.');
        if (baseName === '' || pos < 1) {
            return '';
        }
        return baseName.slice(pos + 1);
    };

    useEffect(() => {
        const fetchSearchResults = async () => {
            try {
                const response = await axiosPrivate.get(`http://localhost:8080/search/${id}`);
                setSearchRes(response.data);
            } catch (error) {
                console.error('Error fetching search results:', error);
                setSearchRes([]);
            }
        };

        fetchSearchResults();
    }, [id, axiosPrivate]);


    const renderFile = async (item) => {
        const extension = getExtension(item.filePath);
        const fileUrl = `http://localhost:8080/files/${item.fileName}/menu/${item.menu.menuId}`;
    
        switch (extension) {
          case 'txt':
            try {
              const textContent = await getFileContent(item.fileName, item.menu.menuId);
              return <pre>{textContent}</pre>;
            } catch (error) {
              console.error('Error rendering text file:', error);
              return <Typography>Error loading text file</Typography>;
            }
          case 'jpg':
          case 'jpeg':
          case 'png':
            try {
              const mediaBlob = await fetchMediaFile(fileUrl);
              if (mediaBlob) {
                const objectURL = URL.createObjectURL(mediaBlob);
                return <img src={objectURL} alt={item.fileName} />;
              } else {
                return <Typography>Error loading image</Typography>;
              }
            } catch (error) {
              console.error('Error rendering image:', error);
              return <Typography>Error loading image</Typography>;
            }
          case 'mp3':
            try {
              const mediaBlob = await fetchMediaFile(fileUrl);
              if (mediaBlob) {
                const objectURL = URL.createObjectURL(mediaBlob);
                return <audio controls><source src={objectURL} type="audio/mpeg" /></audio>;
              } else {
                return <Typography>Error loading audio</Typography>;
              }
            } catch (error) {
              console.error('Error rendering audio:', error);
              return <Typography>Error loading audio</Typography>;
            }
          case 'mp4':
          case 'mov':
            try {
              const mediaBlob = await fetchMediaFile(fileUrl);
              if (mediaBlob) {
                const objectURL = URL.createObjectURL(mediaBlob);
                return <video controls><source src={objectURL} type="video/mp4" /></video>;
              } else {
                return <Typography>Error loading video</Typography>;
              }
            } catch (error) {
              console.error('Error rendering video:', error);
              return <Typography>Error loading video</Typography>;
            }
          case 'pdf':
            try {
              const pdfBlob = await fetchPDFFile(fileUrl);
              if (pdfBlob) {
                const pdfObjectURL = URL.createObjectURL(pdfBlob);
                return (
                  <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
                    <Document file={pdfObjectURL} onLoadSuccess={({ numPages }) => setNumPages(numPages)}>
                      {Array.from(new Array(numPages), (_, index) => (
                        <Page key={`page_${index + 1}`} pageNumber={index + 1} width={window.innerWidth * 0.8} />
                      ))}
                    </Document>
                  </div>
                );
              } else {
                return <Typography>Error loading PDF</Typography>;
              }
            } catch (error) {
              console.error('Error rendering PDF:', error);
              return <Typography>Error loading PDF</Typography>;
            }
          case 'docx':
            return <iframe src={`https://view.officeapps.live.com/op/embed.aspx?src=${fileUrl}`} width="100%" height="600px" frameBorder="0" />;
          case 'xlsx':
            return <iframe src={`https://view.officeapps.live.com/op/embed.aspx?src=${fileUrl}`} width="100%" height="600px" frameBorder="0" />;
          default:
            return <Typography>Unsupported file format</Typography>;
        }
      };

    return (
        <div>
            {searchRes.map((item) => (
                <div key={item.id}>
                    {renderFile(item)}
                </div>
            ))}
        </div>
    );
};

export default View;
