import * as React from 'react';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';
import { Container, Paper } from '@mui/material';
import Button from '@mui/material/Button';

const criterias = [
    {
      value: 'Keywords',
      label: 'Keywords',
    },
    {
      value: 'Author',
      label: 'Author',
    },
  ];

export default function Data() {
    const paperStyle = {padding : '50px 20px', width : '70vw' , margin : '20px auto'}
    const[Search,setSearch] = React.useState('')
    const[Keyword,setKeyword] = React.useState('')
    const[searchResults,setSearchResults] = React.useState([])

    const handleClick = (e)=> {
        const searchVal = {Search,Keyword};
        e.preventDefault()
    
        fetch('http://localhost:8080/data/search',{
            method : 'POST',
            headers : {'Content-Type':'application/json'},
            body : JSON.stringify(searchVal)
        }).then(response => response.json())
        .then(data => {
          setSearchResults(data); // Update the search results state with the received data
        })
        .catch(error => {
          console.log("Some kind of error occured");
        })
    }      

  return (
    <Container>
    <Paper elevation={3} style={paperStyle}>
        <h1 
        style={{fontFamily : 'sans-serif', fontSize : '2em'}}
        > Search the Database</h1>
        <p>Searching for the {Keyword} named {Search}</p>
    <form
      component="form"
      noValidate
      autoComplete="off"
    >
        <TextField
          id="filled-select-currency"
          style={{marginRight : '0.5em', width : '10vw'}}
          select
          label="Select"
          defaultValue="Keywords"
          helperText="Search Category"
          variant="filled"
          value={Keyword}
          onChange={(e)=> setKeyword(e.target.value)}
        >
          {criterias.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))}
        </TextField>
        <TextField
          required
          id="filled-required"
          style={{width : '50vw'}}
          label="Required"
          variant="filled"
          value={Search}
          onChange={(e)=> setSearch(e.target.value)}
        />
    </form>
    <Button variant="contained" onClick={handleClick}>
         Search
        </Button>
    </Paper>

    console.log(searchResults);
    </Container>

  );
}
