import './App.css';
import Appbar from './components/Appbar';
import React, { useState, useEffect } from 'react';

const Result = () => {

  const [deps, setDeps] = useState([]);
  useEffect(() => {
    fetch('http://localhost:8080/search/deps')
      .then(response => response.json())
      .then(data => {
        const departmentsData = data.map(departmentName => ({
          value: departmentName,
          label: departmentName
        }));

        // Add the "All" department option
        departmentsData.push({
          value: 'all',
          label: 'All'
        });

        setDeps(departmentsData);
      })
      .catch(error => {
        console.error('Error fetching departments:', error);
      });
  }, []);


  const [department, setDepartment] = useState('');
  const [func, setFunc] = useState('');
  const [application, setApplication] = useState('');
  const [menu, setMenu] = useState('');
  const [fileName, setFileName] = useState('');
  const [fileType, setFileType] = useState('');
  const [desc, setDesc] = useState('');
  const [auth, setAuth] = useState('');
  // const [file, setFile] = useState(null);


  const handleSubmit = () => {
    // Create a FormData object to store the form data
    const formData = new FormData();
    formData.append('departmentName', department);
    formData.append('functionName', func);
    formData.append('applicationName', application);
    formData.append('menuName', menu);
    // formData.append('file', file);
    formData.append('fileName', fileName);
    formData.append('fileType', fileType);
    formData.append('description', desc);
    formData.append('author', auth);

    // Make a POST request to the backend with the form data
    fetch('http://localhost:8080/add', formData)
      .then(response => {
        // Handle the response
        console.log('Data uploaded successfully:', response.data);
        // Reset the form fields
        setDepartment('');
        setFunc('');
        setApplication('');
        setMenu('');
        setFileName('');
        setFileType('');
        setDesc('');
        setAuth('');
        // setFile(null);
      })
      .catch(error => {
        // Handle the error
        console.error('Error uploading data:', error);
      });
  };

  return (
    <div style={{display :'grid', gridTemplateColumns : '1fr 1fr'}}>
      <div> <Appbar className="App" /> </div>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Department:</label>
          <input type="text" value={department} onChange={e => setDepartment(e.target.value)} />
        </div>
        <div>
          <label>Function:</label>
          <input type="text" value={func} onChange={e => setFunc(e.target.value)} />
        </div>
        <div>
          <label>Application:</label>
          <input type="text" value={application} onChange={e => setApplication(e.target.value)} />
        </div>
        <div>
          <label>Menu:</label>
          <input type="text" value={menu} onChange={e => setMenu(e.target.value)} />
        </div>
        {/* <div>
          <label>File:</label>
          <input type="file" onChange={e => setFile(e.target.files[0])} />
        </div> */}

        <button type="submit">Upload</button>
      </form>
    </div>
  );
};

export default Result;
