import React, { useState, useEffect } from 'react';
import Paper from '@mui/material/Paper';
import { useParams } from 'react-router-dom';
import { Typography } from '@mui/material';
import { styled } from '@mui/system';
import { Link } from 'react-router-dom';
import { Document, Page, pdfjs } from 'react-pdf';
import Appbar from '../components/Appbar';
import Button from '@mui/material/Button';
import useAxiosPrivate from '../hooks/useAxiosPrivate';
import { useNavigate } from "react-router";


const EditPage = () => {

    const axiosPrivate = useAxiosPrivate();
    const navigate = useNavigate();

    const { id } = useParams();
    const [departmentName, setDepartmentName] = useState('');
    const [functionName, setFunctionName] = useState('');
    const [applicationName, setApplicationName] = useState('');
    const [description, setDescription] = useState('');
    const [author, setAuthor] = useState('');
    const [filesToAdd, setFilesToAdd] = useState([]);
    const [filesToDelete, setFilesToDelete] = useState([]);
    const [menuFiles, setMenuFiles] = useState([]);
    const menuId = id;

    useEffect(() => {
        fetchMenuFiles();
    }, [menuId]);

    const myStyle = {
        backgroundImage:
            "url('https://th.bing.com/th/id/R.6f4dd59bf1f5794913c6b0e0f096bf7e?rik=G%2fMZr4l4WxrW%2fw&riu=http%3a%2f%2fi.stack.imgur.com%2f7YKUD.jpg&ehk=oWZpgjcwS0B7wkTMEuTHEawpHqIHFSqjVRcMfKQEwd0%3d&risl=&pid=ImgRaw&r=0')",
        backgroundSize: 'cover',
        backgroundRepeat: 'repeat',
        minHeight : '100vh'
    }

    const fetchMenuFiles = async () => {
        try {
            const response = await axiosPrivate.get(`http://localhost:8080/search/menu/${menuId}`);
            if (response.data) {
                const data = await response.data;
                setMenuFiles(data);
            } else {
                throw new Error('Error fetching menu files');
            }
        } catch (error) {
            console.error('Error fetching menu files:', error);
        }
    };

    const handleFileChange = (event) => {
        setFilesToAdd([...event.target.files]);
    };

    const handleDeleteFile = (fileId) => {
        setFilesToDelete((prevFiles) => [...prevFiles, fileId]);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        const formData = new FormData();
        formData.append('departmentName', departmentName);
        formData.append('functionName', functionName);
        formData.append('applicationName', applicationName);
        formData.append('description', description);
        formData.append('author', author)

        // Append files to add
        for (let i = 0; i < filesToAdd.length; i++) {
            formData.append('filesToAdd', filesToAdd[i]);
        }

        // Append file IDs to delete
        for (let i = 0; i < filesToDelete.length; i++) {
            formData.append('fileIdsToDelete', filesToDelete[i]);
        }

        try {

            const response = await axiosPrivate.post(`http://localhost:8080/update/${menuId}`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });

            if (response?.data) {
                console.log(response);
                navigate("/", { replace: true });
            } else {
                console.log("incorrect submission");
            }
        } catch (error) {
            // Handle the error
            console.error('Error updating menu:', error);
        }
    };

    const intDivs = {
        display: 'grid',
        gridTemplateColumns: '1fr 1fr',
        padding: '0.5em',
    };


    return (
        <div style={myStyle}>
            <div> <Appbar className="App" /> </div>
            <form onSubmit={handleSubmit}>
                <div style={{ backgroundColor: "#eb8934", marginBottom: '3cm', height: '1em' }}></div>
                <Paper style={{
                    display: 'grid',
                    gridTemplateColumns: '1fr 1fr',
                    padding: '0.5em',
                    margin: 'auto',
                    width: 'fit-content'
                }}>
                    <div style={intDivs}>
                        <label>Department Name:</label>
                        <input
                            type="text"
                            value={departmentName}
                            onChange={(event) => setDepartmentName(event.target.value)}
                        />
                    </div>
                    <div style={intDivs}>
                        <label>Function Name:</label>
                        <input
                            type="text"
                            value={functionName}
                            onChange={(event) => setFunctionName(event.target.value)}
                        />
                    </div>
                    <div style={intDivs}>
                        <label>Application Name:</label>
                        <input
                            type="text"
                            value={applicationName}
                            onChange={(event) => setApplicationName(event.target.value)}
                        />
                    </div>
                    <div style={intDivs}>
                        <label>Description:</label>
                        <input
                            type="text"
                            value={description}
                            onChange={(event) => setDescription(event.target.value)}
                        />
                    </div>
                </Paper>
                <Paper style={{
                    display: 'grid',
                    gridTemplateColumns: '1fr 1fr 1fr',
                    padding: '0.5em',
                    margin: 'auto',
                    marginTop: '1em',
                    width: 'fit-content'
                }}>
                    <div>
                        <label>Author:</label>
                        <input
                            type="text"
                            value={author}
                            onChange={(event) => setAuthor(event.target.value)}
                        />
                    </div>
                    <div>
                        <label>Files to Add:</label>
                        <input type="file" multiple onChange={handleFileChange} />
                    </div>
                    <div>
                        <label>Files to Delete:</label>
                        {menuFiles.map((file) => (
                            <div key={file.fileId}>
                                <input
                                    type="checkbox"
                                    value={file.fileId}
                                    onChange={(event) =>
                                        handleDeleteFile(Number(event.target.value))
                                    }
                                />
                                <span>{file.fileName}</span>
                            </div>
                        ))}
                    </div>
                </Paper>
                <Button variant="contained" type='submit' style={{ marginLeft: '45vw' }}>Update</Button>
            </form>
        </div>
    );
};

export default EditPage;
