import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';


const CSVRenderer = ({ csvData }) => {
  const renderCsvData = () => {
    const parsedData = Papa.parse(csvData, {
      header: true, // Treat the first row as headers
    });

    if (parsedData.errors.length === 0) {
      const headers = parsedData.meta.fields.map((field, index) => (
        <th key={index}>{field}</th>
      ));

      console.log("reaching here");

      const rows = parsedData.data.map((row, rowIndex) => (
        <tr key={rowIndex}>
          {parsedData.meta.fields.map((field, colIndex) => (
            <td key={colIndex}>{row[field]}</td>
          ))}
        </tr>
      ));

      return (
        <table>
          <thead>
            <tr>{headers}</tr>
          </thead>
          <tbody>{rows}</tbody>
        </table>
      );
    } else {
      // Handle CSV parsing errors if any
      return <div>Failed to parse CSV data</div>;
    }
  };

  return (
    <div>
      {csvData ? renderCsvData() : <div>Loading CSV data...</div>}
    </div>
  );
};

export default CSVRenderer;
