import * as React from 'react';
import Paper from '@mui/material/Paper';
import Stack from '@mui/material/Stack';
import Box from '@mui/material/Box';
import { styled } from '@mui/system'; // Import from @mui/system instead of @mui/material/styles
import { useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import Appbar from './components/Appbar';

const Details = () => {
  const { id } = useParams();

  const [searchRes, setSearchRes] = useState([]);
  useEffect(() => {
    fetch(`http://localhost:8080/search/${id}`)
      .then(response => response.json())
      .then(data => {
        setSearchRes(data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    flexGrow: 1,
  }));

  const myStyle = {
    //         backgroundImage: 
    //  "url('https://media.geeksforgeeks.org/wp-content/uploads/rk.png')",
    backgroundColor: "#eb8934",
    minHeight: "100vh",
    backgroundSize: 'cover',
    backgroundRepeat: 'repeat',
  }

  return (
    <div style={myStyle}>
        <div> <Appbar className="App" /> </div>
        <div style={{ backgroundColor: "#eb8934", height: '3cm' }}></div>
      {searchRes.map(item => (
        <div key={item.id}>
          <p>File ID: {item.fileId}</p>
          <p>File Name: {item.fileName}</p>
          <p>File Type: {item.fileType}</p>
          <p>Author: {item.author}</p>
          <p>Make Date: {item.makeDate}</p>
          <p>Description: {item.description}</p>

          <h2>Menu Details</h2>
          <p>Menu Name: {item.menu.menuName}</p>

          <h2>Application Details</h2>
          <p>Application Name: {item.menu.application.applicationName}</p>

          <h2>Function Details</h2>
          <p>Function Name: {item.menu.application.function.functionName}</p>

          <h2>Department Details</h2>
          <p>Department Name: {item.menu.application.function.department.departmentName}</p>
        </div>
      ))}
    </div>
  );
};

export default Details;
