import React, { useState, useEffect } from 'react';
import Typography from '@mui/material/Typography';

const TypewriterText = ({ text, speed }) => {
  const [currentText, setCurrentText] = useState('');

  useEffect(() => {
    let animationTimeout;
    let currentIndex = 0;

    const typeWriterAnimation = () => {
      if (currentIndex < text.length) {
        setCurrentText((prevText) => prevText + text.charAt(currentIndex));
        currentIndex++;
        animationTimeout = setTimeout(typeWriterAnimation, speed);
      }
    };

    typeWriterAnimation();

    return () => {
      clearTimeout(animationTimeout);
    };
  }, [text, speed]);

  return <Typography variant="h4" gutterBottom style={{ color: 'white' }}>{currentText}</Typography>;
};

export default TypewriterText;
