import * as React from 'react';
import Paper from '@mui/material/Paper';
import { styled } from '@mui/system'; // Import from @mui/system instead of @mui/material/styles
import { useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import Appbar from './components/Appbar';
import Button from '@mui/material/Button';
import ButtonGroup from '@mui/material/ButtonGroup';
import './index.css';
import TextSnippetIcon from '@mui/icons-material/TextSnippet';
import { Typography } from '@mui/material';
import { Link } from 'react-router-dom';


const View = () => {
    const { id } = useParams();
    function getExtension(path) {
        let baseName = path.split(/[\\/]/).pop(), // extracts file name from full path
          // (supports separators `\\` and `/`)
          pos = baseName.lastIndexOf("."); // gets the last position of `.`
        if (baseName === "" || pos < 1) // if the file name is empty or ...
          return ""; // the dot not found (-1) or comes first (0)
        return baseName.slice(pos + 1); // extracts extension ignoring "."
      }
    const [searchRes, setSearchRes] = useState([]);
    useEffect(() => {
        fetch(`http://localhost:8080/search/${id}`)
            .then(response => response.json())
            .then(data => {
                setSearchRes(data);
            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });

    }, []);


    return (
        <div>
            {searchRes.map(item => (
                <div key={item.id}>
                    <Paper style={{
                        display: 'grid',
                        gridTemplateColumns: '1fr 1fr 1fr',
                        padding: '2em',
                        marginTop: '1em',
                    }}>
                        <div><h4>File Name:</h4> {item.fileName}</div>
                        <div><h4>Make Date:</h4> {item.makeDate}</div>
                        <div>
                            {item.filePath.endsWith('.txt') && (
                                // eslint-disable-next-line jsx-a11y/alt-text
                                <pre>Hello World</pre>
                            )}
                            {item.filePath.endsWith('.jpg') || item.filePath.endsWith('.jpeg') || item.filePath.endsWith('.png') ? (
                                // eslint-disable-next-line jsx-a11y/alt-text
                                <img src={`http://localhost:8080/files/${item.fileName}.` + `${getExtension(item.filePath)}`} />
                            ) : null}
                            {item.filePath.endsWith('.mp4') && (
                                <video controls>
                                    <source src={`http://localhost:8080/files/${item.fileName}.` + `${getExtension(item.filePath)}`} type="video/mp4" />
                                </video>
                            )}
                            {item.filePath.endsWith('.mp3') && (
                                <audio controls>
                                    <source src={`http://localhost:8080/files/${item.fileName}.` + `${getExtension(item.filePath)}`} type="audio/mpeg" />
                                </audio>
                            )}
                        </div>
                    </Paper>
                </div>
            ))}
        </div>
    );
};

export default View;
