import * as React from 'react';
import Paper from '@mui/material/Paper';
import Stack from '@mui/material/Stack';
import Box from '@mui/material/Box';
import { styled } from '@mui/system'; // Import from @mui/system instead of @mui/material/styles
import { useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import Appbar from './components/Appbar';
import { Grid } from '@mui/material';
import './index.css';

const Details = () => {
  const { id } = useParams();

  const [searchRes, setSearchRes] = useState([]);
  useEffect(() => {
    fetch(`http://localhost:8080/search/${id}`)
      .then(response => response.json())
      .then(data => {
        setSearchRes(data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    flexGrow: 1,
  }));

  const myStyle = {
    //         backgroundImage: 
    //  "url('https://media.geeksforgeeks.org/wp-content/uploads/rk.png')",
    backgroundColor: "#eb8934",
    minHeight: "100vh",
    backgroundSize: 'cover',
    backgroundRepeat: 'repeat',
  }

  return (
    <div style={myStyle}>
        <div> <Appbar className="App" /> </div>
        <div style={{ backgroundColor: "#eb8934", height: '3cm' }}></div>
      {searchRes.map(item => (
        <div key={item.id}>
          <Paper style={{display : 'grid', gridTemplateColumns : '1fr 1fr 1fr', 
          padding :'2em'}}>
            <div><h4>Department Name: </h4> {item.menu.application.function.department.departmentName}</div>
            <div><h4>Function Name:</h4>{item.menu.application.function.functionName}</div>
            <div><h4>Application Name : </h4> {item.menu.application.applicationName}</div>
            <div><h4>Menu Name:</h4>  {item.menu.menuName}</div>
          <div><h4>File Name:</h4> {item.fileName}</div>
          <div><h4>Author: </h4>{item.author}</div>
          <div><h4>Make Date:</h4> {item.makeDate}</div>
          <div><h4>Description:</h4> {item.menu.description}</div>
          </Paper>
        </div>
      ))}
    </div>
  );
};

export default Details;
