import React, { useState } from 'react';
import { TextField, Button, Typography, Paper, Box } from '@mui/material';
import axios from 'axios';

const Signup = () => {
    const [userName, setUserName] = useState('');
    const [passWord, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    const handleSignup = async () => {
        try {
            const response = await axios.post(`http://localhost:8080/login/create?userName=${userName}&passWord=${passWord}`);

            if (response.status === 200) {
                setErrorMessage('');
                setSuccessMessage('User created successfully!');
            }
        } catch (error) {
            if (error.response && error.response.status === 409) {
                setErrorMessage('A user with the username already exists. Please choose another username.');
                setSuccessMessage('');
            } else {
                setErrorMessage('Error occurred while creating the user.');
                setSuccessMessage('');
            }
        }
    };

    const myStyle = {
        display: 'flex',
        justifyContent: "center",
        backgroundImage: "url(https://images.unsplash.com/photo-1486406146926-c627a92ad1ab?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=870&q=80)",
        backgroundSize: "cover",
        alignItems: "center",
        height: "100vh"
    }

    return (
        <Box
        style={myStyle}
        >
            <Paper elevation={3} style={{ padding: '2em', width: '400px' }}>
                <Typography variant="h5" align="center">Sign Up to Nucleopedia</Typography>
                <TextField
                    label="Username"
                    value={userName}
                    onChange={(e) => setUserName(e.target.value)}
                    fullWidth
                    margin="normal"
                />
                <TextField
                    label="Password"
                    value={passWord}
                    onChange={(e) => setPassword(e.target.value)}
                    type="password"
                    fullWidth
                    margin="normal"
                />
                <Button variant="contained" color="primary" onClick={handleSignup} fullWidth>
                    Sign Up
                </Button>
                {errorMessage && (
                    <Box
                        bgcolor="error.main"
                        color="error.contrastText"
                        mt={2}
                        p={2}
                        borderRadius={4}
                        textAlign="center"
                    >
                        {errorMessage}
                    </Box>
                )}
                {successMessage && (
                    <Box
                        bgcolor="success.main"
                        color="success.contrastText"
                        mt={2}
                        p={2}
                        borderRadius={4}
                        textAlign="center"
                    >
                        {successMessage}
                    </Box>
                )}
            </Paper>
        </Box>
    );
};

export default Signup;
