import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import SwipeableDrawer from '@mui/material/SwipeableDrawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';

export default function Appbar() {

  const [dep,setDep] = React.useState()
  const criterias = [
    {
      value: 'Captive Auto',
      label: 'Captive Auto',
    },
    {
      value: 'Global Sucess',
      label: 'Global Sucess',
    },
  ];

  const drawerClick = (text)=> {
      if (text==="Select Department"){

      } else if (text==="Add New Data"){

      } else if (text==="Edit a Record"){

      }
    }

  const [state, setState] = React.useState({
    left: false
  });

  const toggleDrawer = (anchor, open) => (event) => {
    if (
      event &&
      event.type === 'keydown' &&
      (event.key === 'Tab' || event.key === 'Shift')
    ) {
      return;
    }

    setState({ ...state, [anchor]: open });
  };

  const list = (anchor) => (
    <Box
      sx={{ width: anchor === 'top' || anchor === 'bottom' ? 'auto' : 250 }}
      role="presentation"
      onClick={toggleDrawer(anchor, false)}
      onKeyDown={toggleDrawer(anchor, false)}
    >
      <List>
      <TextField
          id="filled-select-currency"
          style={{marginRight : '0.5em', width : '10vw'}}
          select
          label="Select"
          defaultValue="Keywords"
          helperText="Search Category"
          variant="filled"
          value={dep}
          onChange={(e)=> setDep(e.target.value)}
        >
          {criterias.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))}
        </TextField>
        {['Add New Data','Edit a Record'].map((text) => (
          <ListItem key={text} disablePadding>
            <ListItemButton onClick={drawerClick({text})}>
              <ListItemText primary={text} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </Box>
  );
  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="fixed">
        <Toolbar>
        <div>
          {['left'].map((anchor) => (
            <React.Fragment key={anchor}>
              <Button onClick={toggleDrawer(anchor, true)} style={{color : 'white'}}>Menu</Button>
              <SwipeableDrawer
                anchor={anchor}
                open={state[anchor]}
                onClose={toggleDrawer(anchor, false)}
                onOpen={toggleDrawer(anchor, true)}
              >
                {list(anchor)}
              </SwipeableDrawer>
            </React.Fragment>
          ))}
        </div>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Nucleopedia
          </Typography>
          <Button color="inherit">Logout</Button>
        </Toolbar>
      </AppBar>
    </Box>
  );
}
